import React from 'react';
import {connect} from 'react-redux';import {reduxForm, Field, SubmissionError, focus} from 'redux-form';
import Input from './input';
import {addingNewPawfile, addingNewForm} from '../../actions/index';


export  class AddNewPawfileForm extends React.Component{

  componentWillUnmount(){
    this.props.dispatch(addingNewForm(false));
  }


  onSubmit(values){
    this.props.dispatch(addingNewPawfile(values));
  }

  render(){
    return(
      <div>
          <h2>Create A New Pawfile</h2>
          <form onSubmit={this.props.handleSubmit(values => this.onSubmit(values))}>

            <Field
              component={Input}
              label="Pet's Name:" 
              type="text" 
              name="name" 
              id="name"
              /> 

            <Field
              component={Input} 
              label = "Species"
              name="species" 
              id="species"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Breed"
              name="breed" 
              id="breed"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Gender"
              name="gender" 
              id="gender"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Birthday"
              name="birthday" 
              id="birthday"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Bio"
              name="bio" 
              id="bio"
              type = "textarea"
              >
            </Field>

            <Field
              component={Input} 
              label = "Image URL:"
              name="img" 
              id="img"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Date"
              name="date" 
              id="date"
              type = "date"
              >
            </Field>

            <button type="submit">Submit</button>
          </form>
        </div>
    );
  }
}

export default reduxForm({
  form: 'addingPawfileForm',
})(AddNewPawfileForm);
