import React from 'react';
import {connect} from 'react-redux';
import {reduxForm, Field, Fieldset, SubmissionError, focus} from 'redux-form';
import Input from './input';
import {submitNewPawfile, showPawfileForm} from '../../actions/index';
import {required, nonEmpty, unSelected} from './validators';

import './pawfile-form.css';

export class PawfileForm extends React.Component{

  componentWillUnmount(){
    this.props.dispatch(showPawfileForm(false, undefined));
  }

  onSubmit(values){
    console.log('values are', values);
    this.props.dispatch(submitNewPawfile(values));
    this.props.dispatch(showPawfileForm(false, undefined));
  }

  render(){
    console.log('the props in redux form is', this.props);

    return(
      <div className='new-pawfile-form-modal'>
          <form className="new-pawfile-form blurb" onSubmit={this.props.handleSubmit(values => this.onSubmit(values))}>
          <button type="button" className = "close" onClick={()=>this.props.dispatch(showPawfileForm(false, undefined))}>X</button>
          <h2>New Pawfile</h2>

            <Field
              component={Input}
              label="Name:" 
              type="text" 
              name="name" 
              id="name"
              validate={[required, nonEmpty]}
              /> 

            <Field
              component={Input}
              label="Image URL:" 
              type="url" 
              name="img" 
              id="img"
              validate={[required, nonEmpty]}
              >
            </Field>

            <Field
              component={Input} 
              label = "Species:"
              name="species" 
              id="species"
              type = "text"
              element="select"
              validate={[unSelected]}
              >
              <option value="" className="bold"> Select a Species</option>
              <option value="Dog">Dog</option>
              <option value="Cat">Cat</option>
              <option value="Bird">Bird</option>
            </Field>

            <Field
              component={Input} 
              label = "Gender:"
              name="gender" 
              id="gender"
              element="select"
              type = "text"
              validate={[unSelected]}
              >
              <option value=""> Select a Gender</option>
              <option value="female">Female</option>
              <option value="male">Male</option>
            </Field>

            <Field
              component={Input} 
              label = "Breed:"
              name="breed" 
              id="breed"
              type = "text"
              >
            </Field>

            <Field
              component={Input} 
              label = "Birthday:"
              name="birthday" 
              id="birthday"
              type = "date"
              >
            </Field>

            <Field
              component={Input} 
              element="textarea"
              label = "Bio:"
              name="bio" 
              id="bio"
              type = "textarea"
              className="test"
              >
            </Field>

            <button type="submit">Save Pawfile</button>
            <button onClick={()=>this.props.dispatch(showPawfileForm(false, undefined))} type="cancel">Cancel</button>
          </form>
        </div>
    );
  }
}

function mapStateToProps(state) {
  console.log('in form the id  is', state.pawfile.pawfiles);
  return {
    initialValues: {
      name: state.pawfile.pawfiles.currentPetId ? state.pawfiles[state.pawfile.pawfiles.currentPetId].name : "",
    }
  }
}

export default connect(mapStateToProps)(reduxForm({
  form:'PawfileForm',
})(PawfileForm));


