import React from 'react';
import {connect} from 'react-redux';import {reduxForm, Field, SubmissionError, focus} from 'redux-form';
// import Input from './input';

export  class AddNewPawfileForm extends React.Component{
  onSubmit(values){
    console.log(values);
  }

  render(){
    return(
      <div>
          <h2>Create A New Pawfile</h2>
          <form onSubmit={this.props.handleSubmit(values => this.onSubmit(values))}>

            <Field
              // component={Input}
              label="Pet's Name:" 
              type="text" 
              name="name" 
              id="name"
              /> 

            <Field
              // component={Input} 
              label = "Species"
              name="species" 
              id="species"
              type = "text"
              >
            </Field>


            <Field 
              // component={Input} 
              element="textarea"
              type="text" 
              name="details" 
              id="details"
              label='Comments'
              /> 

            <button type="submit">Submit</button>
          </form>
        </div>
    );
  }

}

export default reduxForm({
  form: 'delivery-form',
})(DeliveryForm);
