import React from 'react';
import {connect} from 'react-redux';import {reduxForm, Field, SubmissionError, focus} from 'redux-form';
import Input from './input';

export default class AddNewPawfileForm extends React.Component{
  onSubmit(values){
    console.log(values);
  }

  render(){
    return(
      <div>
          <h2>Create A New Pawfile</h2>
          <form onSubmit={this.props.handleSubmit(values => this.onSubmit(values))}>

            <Field
              component={Input}
              label="Tracking Number:" 
              type="text" 
              name="trackingNumber" 
              id="trackingNumber"
              validate={[required, nonEmpty, five, num]}
              aria-describedby="instructions"
              /> 
              <div id="instructions">
                  The tracking number is a five-digit number.
              </div>
              <div id="more-instructions">
                  It's on the box, gurl
              </div>

            <Field
              component={Input} 
              label = "Issue?"
              name="issue" 
              id="issue"
              element = "select"
              validate={[unSelected]}
              defaultValue=""
              >
              <option value=""> Select an option</option>
              <option value="not-delivered">My delivery hasn't arrived</option>
              <option value="wrong-item">Wrong item</option>
              <option value ="missing-part">Missing</option>
            </Field>


            <Field 
              component={Input} 
              element="textarea"
              type="text" 
              name="details" 
              id="details"
              label='Comments'
              /> 

            <button type="submit">Submit</button>
          </form>
        </div>
    );
  }

}
