import React from 'react';
import {connect} from 'react-redux';

import './main-section.css'

export function MainSection(props){
    return(
      <main>
        <nav class="options">
            <div class="add-buttons">
              <button class="new-memory">New Memory</button>
              <button class="new-medical">New Medical</button>
            </div>
            <div class="filters">
              <select class="filter-category" name="" id="">
                <option value="">Select Category</option>
                <option value="">Memories</option>
                <option value="">Medical</option>
              </select>
              <select class="filter-date" name="" id="">
                <option value="">Select Date</option>
              </select>
            </div>
            <div class="search-input">
              <input class="search" type="search" placeholder="Search Posts"/>
            </div>
          </nav>
      </main>
    );
}

const mapStateToProps = (state,props) => ({
  pawfile: state.pawfile.pawfiles[props.id],
});

export default connect(mapStateToProps)(MainSection);
