export const ADDING_NEW_FORM = 'ADDING_NEW_FORM';
export const addingNewForm = (bool) => ({
    type: ADDING_NEW_FORM,
    bool
});

export const EDITING_PAWFILE_FORM = 'EDITING_PAWFILE_FORM';
export const editingPawfileForm = (id) => ({
    type: ADDING_NEW_FORM,
    id
});

