import React from 'react';
import {connect} from 'react-redux';
import {formatDate, stringToArrayList} from '../helper-functions';
import './medical-post.css'

export function MedicalPost(props){
  return(
    <li key={props.index} className={`${props.type.toLowerCase()} post`}>
      <h3 className="post-title">{props.title}</h3>
      <span className="post-date">{formatDate(props.date).toLocaleDateString()}</span>
      {props.symptoms && <ul className="post-symptoms">
        <strong>Symptoms:</strong>
        {stringToArrayList(props.symptoms).map((symptom, index)=>(
        <li key={index}>{symptom}</li>
        ))}
      </ul>}
      {props.vaccinations && <ul className="post-vaccinations">
        <strong>Vaccinations:</strong>
        {stringToArrayList(props.vaccinations).map((vaccination, index)=>(
        <li key={index}>{vaccination}</li>
        ))}
      </ul>}
      {props.notes && <p> <strong>Notes:</strong> {props.notes}</p> }
      {props.doctor && <p><strong>Doctor:</strong> {props.doctor}</p>}
    </li>
  );
}

export default connect()(MedicalPost);
