import React from 'react';
import {connect} from 'react-redux';
import MemoryPost from './memory-post'
import MedicalPost from './medical-post'
import {showMedicalForm, showMemoryForm, changeSearchTerm} from '../../actions/index';

import './main-section.css'

export function MainSection(props){
  let posts;

  if(props.specificPawfile.posts){
     posts = props.specificPawfile.posts.map((post,index)=>(
      post.type==="memory" ?
        <MemoryPost key={index} {...post}/>
     :
      <MedicalPost key={index} {...post}/>
    ))
    console.log('the posts are', posts); //why does it look weird 
    //automatically sort by newest to oldest
    posts.sort((a,b)=> new Date(b.props.date) - new Date(a.props.date))

    if(props.currentSearchTerm){
      posts = posts.filter(post=>
        post.props.title.toLowerCase().includes(props.currentSearchTerm) || post.props.date.toLowerCase().includes(props.currentSearchTerm) || 
        (post.props.description && post.props.description.toLowerCase().includes(props.currentSearchTerm)) || 
        (post.props.notes && post.props.notes.toLowerCase().includes(props.currentSearchTerm)) || 
        (post.props.doctor && post.props.doctor.toLowerCase().includes(props.currentSearchTerm)) || 
        (post.props.symptoms && post.props.symptoms.find(symptom=>symptom.toLowerCase().includes(props.currentSearchTerm))) || 
        (post.props.prescriptions && post.props.prescriptions.find(prescription=>prescription.toLowerCase().includes(props.currentSearchTerm)))
        )
    }
  }
  console.log('rerendering main section')
    return(
      <main>
        <nav className="options">
            <div className="add-buttons">
              <button onClick={()=>props.dispatch(showMemoryForm(true))} className="new-memory">New Memory</button>
              <button onClick={()=>props.dispatch(showMedicalForm(true))} className="new-medical">New Medical</button>
            </div>
            <div className="filters">
              <select className="filter-category" name="" id="">
                <option value="">Filter by Category</option>
                <option value="">Memories</option>
                <option value="">Medical</option>
              </select>
            </div>
            <div className="search-input">
              <input onChange={e=>props.dispatch(changeSearchTerm(e.target.value))} className="search" type="search" placeholder='Search Posts'/>
            </div>
          </nav>
          <ul className="posts">
              {posts}
          </ul>
      </main>
    );
}

const mapStateToProps = (state,props) => ({
  pawfiles: state.pawfile.pawfiles,
  specificPawfile: state.pawfile.pawfiles[props.id],
  currentSearchTerm: state.pawfile.currentSearchTerm
});

export default connect(mapStateToProps)(MainSection);
